import chalk from 'chalk';
import { writeFile } from 'fs/promises';
import { scanLocalSkills } from '../config.js';
export async function exportCommand(options) {
    const outputFile = options.output || 'clawpack.json';
    console.log(chalk.blue('ðŸ“¦ Scanning local skills...'));
    const skills = await scanLocalSkills();
    const manifest = {
        version: '1.0.0',
        exportedAt: new Date().toISOString(),
        platform: 'openclaw',
        skills,
    };
    await writeFile(outputFile, JSON.stringify(manifest, null, 2));
    console.log(chalk.green(`âœ“ Exported ${skills.length} skill(s) to ${outputFile}`));
}
